		  			var s1 = i ? -1 : 1,
		  			    s2 = i ? w1 : w0,
		  			    t1 = w1 * w1 - w0 * w0 + s1 * rho2 * rho2 * u1 * u1,
		  			    b1 = 2 * s2 * rho2 * u1,
		  			    b = t1 / b1,
		  			    sq = Math.sqrt(b * b + 1) - b;

